'use strict';

import React from 'react';

class ColumnDefinition extends React.Component {
  render() {
    return null;
  }
}

ColumnDefinition.PropTypes = {
  //this is the name of the column that this definition applies to.
  //This used to be known as columnName
  id: React.PropTypes.string.isRequired,
  //The order that this column appears in. If not specified will just use the order that they are defined
  order: React.PropTypes.number,
  //Determines whether or not the user can disable this column from the settings.
  locked: React.PropTypes.bool,
  //The css class name to apply to the header for the column
  headerCssClassName: React.PropTypes.string,
  //The css class name to apply to this column.
  cssClassName: React.PropTypes.string,
  //The display name for the column. This is used when the name in the column heading and settings should be different from the data passed in to the Griddle component.
  displayName: React.PropTypes.string,
  //The component that should be rendered instead of the standard column data. This component will still be rendered inside of a TD element.
  customComponent: React.PropTypes.object,
  //The component that should be used instead of the normal title
  customHeaderComponent: React.PropTypes.object,
  //Can this column be sorted
  sortable: React.PropTypes.bool,
  //What sort type this column uses - magic string :shame:
  sortType: React.PropTypes.string,
  //Any extra data that should be passed to each instance of this column
  extraData: React.PropTypes.object,
  //The width of this column -- this is string so things like % can be specified
  width: React.PropTypes.string
};

export default ColumnDefinition;

import React from 'react';
import classnames from 'classnames';

import { getStyleProperties } from './utils/styleHelper';

class Column extends React.Component {
  constructor(props, context) {
    super(props, context);
  }

  shouldComponentUpdate(nextProps) {
    if(this.props.forceUpdate) { return true; }
    if(this.props.value === nextProps.value) {
      return false;
    }

    return true;
  }

  render() {
    //TODO: this is temporary -- we'll need to merge styles or something
    //  why not use the getStyle from defaultStyles?
    const styles = this._getStyles();

    const { className } = getStyleProperties(this.props, 'column');
    const classNames = classnames(className, this.props.cssClassName);

    return (
      <td
        style={styles}
        key={this.props.dataKey}
        rowIndex={this.props.rowIndex}
        onClick={this._handleClick}
        onMouseOver={this._handleHover}
        className={classNames}>
          {this.props.hasOwnProperty('customComponent') ?
            <this.props.customComponent
              data={this.props.value}
              rowData={this.props.rowData}
              originalData={this.props.originalRowData}
              rowIndex={this.props.rowIndex}
              absoluteRowIndex={this.props.absoluteRowIndex}
              extraData={this.props.extraData} /> :
            this.props.value}
      </td>
    );
  }

  //TODO: Figure out a way to get this hooked up with the normal styles methods
  //maybe a merge styles property or something along those lines
  _getStyles = () => {
    const style = this.props.styles.getStyle({
      styles: this.props.styles.inlineStyles,
      styleName: 'column',
      //todo: make this nicer
      mergeStyles: {
          ...((this.props.width || this.props.alignment || this.props.styles) ?
            Object.assign({ width: this.props.width || null, textAlign: this.props.alignment }) : {}),
          ...this.props.style
      }
    });

    return style;
  }

  _handleClick = (e) => {
    if (this.props.onClick) this.props.onClick(e);

    this.props.events.columnClick(this.props.dataKey, this.props.value, this.props.rowIndex, this.props.rowData);
  }

  _handleHover = (e) => {
    this.props.events.columnHover(this.props.dataKey, this.props.value, this.props.rowIndex, this.props.rowData);
  }
}

Column.defaultProps = {
  columnProperties: {
    cssClassName: ''
  }
};

Column.propTypes = {
  alignment: React.PropTypes.oneOf(['left', 'right', 'center']),
  columnHover: React.PropTypes.func,
  columnClick: React.PropTypes.func
};

export default Column;

import ColumnDefinition from './column-definition';
import Column from './column';
import Filter from './filter';
import NoResults from './no-results';
import Loading from './loading';
import Pagination from './pagination';
import RowDefinition from './row-definition';
import Row from './row';
import SettingsToggle from './settings-toggle';
import Settings from './settings';
import TableBody from './table-body';
import TableHeading from './table-heading';
import TableHeadingCell from './table-heading-cell';
import Table from './table';

export { ColumnDefinition as ColumnDefinition }
export { Column as Column }
export { Filter as Filter }
export { NoResults as NoResults }
export { Loading as Loading }
export { Pagination as Pagination }
export { RowDefinition as RowDefinition}
export { Row as Row }
export { SettingsToggle as SettingsToggle }
export { Settings as Settings }
export { TableBody as TableBody }
export { TableHeading as TableHeading }
export { TableHeadingCell as TableHeadingCell }
export { Table as Table }

export default {
  useGriddleStyles: true,
  useGriddleClassNames: false,
  useFixedTable: true,
  pageSize: 10
}

//This is a method that the individual components will use to interact with the inline styles
//
// styleName: the name of the inline style
// styles: the inline styles object
// useStyles: whether or not the inline styles should be used
// mergeStyles: styles to apply in addition to the inline styling. This is usually applied with some logic in the front-end
export function getStyle({styleName, styles, mergeStyles = null}) {
  if (styles.hasOwnProperty(styleName)) {
    return Object.assign({}, styles[styleName], mergeStyles);
  }

  return mergeStyles || null;
}

export function getClassName({section, classNames}) {
  if(classNames.hasOwnProperty(section)) {
    return classNames[section];
  }

  return null;
}

export const inlineStyles = {
  settingsToggle: {},
  filter: {},
  columnTitle: {},
  column: {},
  pagination: {},
  table: {},
  fixedTable: {
    tableLayout: 'fixed',
  }
}

export const griddleStyles = {
  settingsToggle: {
    background: 'none',
    border: 'none',
    padding: 0,
    margin: 0,
    fontSize: 14,
    width: '50%',
    textAlign: 'right'
  },

  filter: {
    width: '50%',
    textAlign: 'left',
    color: '#222',
    minHeight: '1px'
  },

  columnTitle: {
    backgroundColor: '#EDEDEF',
    border: '0',
    borderBottom: '1px solid #DDD',
    color: '#222',
    padding: '5px',
    cursor: 'pointer'
  },

  column: {
    margin: '0',
    padding: '5px 5px 5px 5px',
    backgroundColor: '#FFF',
    borderTopColor: '#DDD',
    color: '#222'
  },

  pagination: {
    'padding' : 5,
    border: '0',
    backgroundColor: '#EDEDED',
    color: '#222',
    width: '100%',
    textAlign: 'center'
  },

  table: {
    width: '100%'
  },

  fixedTable: {
    tableLayout: 'fixed',
  }
}

export const griddleClassNames = {
  column: 'griddle-column',
  filter: 'griddle-filter',
  noResults: 'griddle-noResults',
  loading: 'griddle-loadingResults',
  pagination: 'griddle-pagination',
  rowDefinition: 'griddle-row-definition',
  row: 'griddle-row',
  settingsToggle: 'griddle-settings-toggle',
  settings: 'griddle-settings',
  tableBody: 'griddle-table-body',
  tableHeading: 'griddle-table-heading',
  tableHeadingCell: 'griddle-table-heading-cell',
  table: 'griddle-table'
}

export const classNames = {
  column: null,
  filter: null,
  noResults: null,
  loading: null,
  pagination: null,
  rowDefinition: null,
  row: null,
  settingsToggle: null,
  settings: null,
  tableBody: null,
  tableHeading: null,
  tableHeadingCell: null,
  table: null
}

export const icons = {
  //TODO: these need to get moved to something that adds these on as part of the subgrid 'plugin'
  parentRowCollapsed: '▶',
  parentRowExpanded: '▼',
  sortDescending: '▼',
  sortAscending: '▲'
};

export function getAssignedStyles(extension, useGriddleStyles) {
  let styles = {
    inlineStyles: useGriddleStyles ? Object.assign({}, inlineStyles, griddleStyles) : inlineStyles,
    classNames,
    icons,
    getStyle,
    getClassName
  };

  if (extension) {
    if (extension.hasOwnProperty('inlineStyles')) {
      styles.inlineStyles = Object.assign({}, styles.inlineStyles, extension.inlineStyles);
    }

    if (extension.hasOwnProperty('classNames')) {
      styles.classNames = Object.assign({}, styles.classNames, extension.classNames);
    }

    if (extension.hasOwnProperty('icons')) {
      styles.icons = Object.assign({}, icons, extension.icons);
    }
  }

  return styles;
}

import React from 'react';
import { getStyleProperties }  from './utils/styleHelper';

class Filter extends React.Component {
  constructor(props, context) {
    super(props, context);

    this._handleChange = this._handleChange.bind(this);
  }

  shouldComponentUpdate(props) {
    return false;
  }

  render() {
    const {style, className } = getStyleProperties(this.props, 'filter');

    return (
      <input
        type="text"
        name="filter"
        className={className}
        style={style}
        placeholder="filter"
        onChange={this._handleChange} />
    );
  }

  _handleChange = (e) => {
    //TODO: debounce this
    this.props.events.setFilter(e.target.value);
  }
}

Filter.propTypes = {
  setFilter: React.PropTypes.func
}

export default Filter;

import React, { Component, PropTypes } from 'react'
import { getStyleProperties }  from './utils/styleHelper'

export default class Loading extends Component {
  render() {
    const { style, className } = getStyleProperties(this.props, 'loading')

    return (
      <tr>
        <td>
          <div style={style} className={className}>
            <h4>Loading...</h4>
          </div>
        </td>
      </tr>
    )
  }
}

import React from 'react';
import {getStyleProperties}  from './utils/styleHelper';

class NoResults extends React.Component {
  render() {
    const { style, className } = getStyleProperties(this.props, 'noResults');

    return (
      <div style={style} className={className}>
        <h4>No results found.</h4>
      </div>
    )
  }
}

export default NoResults;

import React from 'react'
import { getStyleProperties } from './utils/styleHelper';

class Pagination extends React.Component {
  constructor(props, context) {
    super(props, context);

    this._handleChange = this._handleChange.bind(this);
  }

  render() {
    const {style, className } = getStyleProperties(this.props, 'pagination');

    return (<div className={className} style={style}>
      {this.props.hasPrevious ? <button onClick={this.props.events.getPreviousPage}>Previous</button> : null }
      {this._getSelect()}
      {this.props.hasNext ? <button onClick={this.props.events.getNextPage}>Next</button> : null }
    </div>);
  }

  _handleChange(e) {
    this.props.events.getPage(parseInt(e.target.value));
  }

  _getSelect() {
    if( !this.props.pageProperties || !this.props.pageProperties.maxPage ) { return; }
    //Make this nicer
    var options = [];

    for(var i = 1; i <= this.props.pageProperties.maxPage; i++) {
      options.push(<option value={i} key={i}>{i}</option>)
    }

    return <select onChange={this._handleChange} value={this.props.pageProperties.currentPage}>{options}</select>
  }
}

export default Pagination;

'use strict';

import React from 'react';
import ColumnDefinition from './column-definition';

class RowDefinition extends React.Component {
  render() {
    return null;
  }
}

RowDefinition.propTypes = {
  //Children can be either a single column definition or an array
  //of column definition objects
  //TODO: get this prop type working again
  /*children: React.PropTypes.oneOfType([
    React.PropTypes.instanceOf(ColumnDefinition),
    React.PropTypes.arrayOf(React.PropTypes.instanceOf(ColumnDefinition))
  ]),*/
  //The column value that should be used as the key for the row
  //if this is not set it will make one up (not efficient)
  keyColumn: React.PropTypes.string,

  //The column that will be known used to track child data
  //By default this will be "children"
  childColumnName: React.PropTypes.string,

  //This property allows an to set a css class on a row based on
  //the data within. This should return a css-class name
  cssFunction: React.PropTypes.func
}

export default RowDefinition;

import React from 'react';
import ColumnHelper from './utils/column-helper';
import { getStyleProperties } from './utils/styleHelper';

class Row extends React.Component {
  constructor(props, context) {
    super(props, context);
  }

  render() {
    //TODO: Refactor this  -- the logic to show / hide columns is kind of rough
    //      Also, it seems that if we moved this operation to a store, it could be a bit faster
    let columns = [];
    const { columnProperties,
      ignoredColumns,
      tableProperties,
      rowData,
      events,
      originalRowData,
      rowIndex,
      absoluteRowIndex } = this.props;
    const { griddleKey } = rowData.__metadata;

    //render just the columns that are contained in the metdata
    for (var column in rowData) {
      //get the additional properties defined in the creation of the object
      let columnProperty = ColumnHelper.getColumnPropertyObject(columnProperties, column);
      //render the column if there are no properties, there are properties and the column is in the collection OR there are properties and no column properties.
      if(ColumnHelper.isColumnVisible(column, {columnProperties, ignoredColumns: ignoredColumns || []})) {
        columns.push(<this.props.components.Column
          {...this.props}
          key={column}
          originalRowData={originalRowData}
          absoluteRowIndex={absoluteRowIndex}
          dataKey={column}
          value={rowData[column]}
          {...columnProperty}
           />);
      }
    }

    const { style, className } = getStyleProperties(this.props, 'row');
    return (
      <tr
        style={style}
        className={className}
        onMouseOver={this._handleHover}
        onClick={this._onClick}
        key={griddleKey}
      >
        {columns}
      </tr>
    );
  }

  //TODO: this can go -- double confirm that nothing is using it
  _handleHover = (e) => {
    this.props.events.rowHover(this.props.rowIndex, this.props.rowData);
  }

  //TODO: this can go -- double confirm that nothing is using it
  _handleSelect = (e) => {
    this.props.events.rowSelect(this.props.rowIndex, this.props.rowData);
  }

  _onClick = () => {
    this.props.events.rowClick(this.props.rowData, this.props.originalRowData)
  }
}

export default Row;

import React from 'react';
import { getStyleProperties } from './utils/styleHelper';
import classnames from 'classnames';

class SettingsToggle extends React.Component {
  constructor() {
    super();
    this.state = {};
    this.state.toggled = false;

    this._handleButton = this._handleButton.bind(this);
  }

  shouldComponentUpdate() {
    return false;
  }

  render() {
    const { style, className } = getStyleProperties(this.props, 'settingsToggle');
    const toggleClass = classnames(this.state.toggled ? 'toggled' : 'not-toggled', className);

    return <button className={toggleClass} style={style} onClick={this._handleButton}>Settings</button>;
  }

  //this should keep track locally if it's toggled
  //and just send whether or not settings should be shown
  _handleButton() {
    const toggled = !this.state.toggled;
    this.props.showSettings(toggled);
    this.setState({toggled: toggled});
  }
}

export default SettingsToggle;

import React from 'react';
import { getStyleProperties } from './utils/styleHelper';

class CheckItem extends React.Component {
  render() {
    return (
      <label onClick={this._handleClick}>
        <input type="checkbox" checked={this.props.checked} name={this.props.name} />
        {this.props.text}
      </label>);
  }

  _handleClick = () => {
    this.props.toggleColumn(this.props.name);
  }
}

CheckItem.propTypes = {
  toggleColumn: React.PropTypes.func.isRequired,
  name: React.PropTypes.string.isRequired,
  checked: React.PropTypes.bool,
  value: React.PropTypes.oneOfType([
    React.PropTypes.string,
    React.PropTypes.Number
  ]),
  text: React.PropTypes.string
};

class PageSize extends React.Component {
  render() {
    return (
      <select name="pageSize" onChange={this._handleChange}>
        {this.props.sizes.map(size => <option value={size}>{size}</option>)}
      </select>
    );
  }

  _handleChange = (e) => {
    this.props.setPageSize(parseInt(e.target.value));
  }
}

PageSize.defaultProps = {
  sizes: [5, 10, 20, 30, 50, 100]
}
class Settings extends React.Component {
  render() {
    const keys = Object.keys(this.props.renderProperties.columnProperties);
    const { style, className } = getStyleProperties(this.props, 'settings');

    var columns = this.props.allColumns.map(column =>
      <CheckItem
        toggleColumn={this.props.events.toggleColumn}
        name={column}
        text={this._getDisplayName(column)}
        checked={keys.indexOf(column) > -1} />);

    return (
      <div style={style} className={className}>
        {columns}
        <PageSize setPageSize={this.props.events.setPageSize}/>
      </div>
    );
  }

  _getDisplayName = (column) => {
    const { renderProperties } = this.props;
    if(renderProperties.columnProperties.hasOwnProperty(column)) {
      return renderProperties.columnProperties[column].hasOwnProperty('displayName') ?
        renderProperties.columnProperties[column].displayName :
        column
    } else if (renderProperties.hiddenColumnProperties.hasOwnProperty(column)) {
    return renderProperties.hiddenColumnProperties[column].hasOwnProperty('displayName') ?
        renderProperties.hiddenColumnProperties[column].displayName :
        column
    }

    return column;
  }
}

Settings.propTypes = {
  allColumns: React.PropTypes.arrayOf(React.PropTypes.string),
  visibleColumns: React.PropTypes.arrayOf(React.PropTypes.node)
};

export default Settings;

import React from 'react';
import { getStyleProperties } from './utils/styleHelper';

class TableBody extends React.Component {
  constructor(props, context) {
    super(props, context);
  }

  shouldComponentUpdate(nextProps) {
    return this.props.data !== nextProps.data;
  }

  render() {
    const { data, loading, components, styles, settings, events, renderProperties, tableProperties } = this.props;

    const rows = loading ? <components.Loading components={components} styles={styles} settings={settings} events={events} />
        : data
          .filter(data => data.visible === undefined || data.visible === true)
          .map((data, index) =>
            <this.props.components.Row rowData={data}
              absoluteRowIndex={data.__metadata.index}
              key={data.__metadata.griddleKey}
              components={components}
              events={events}
              rowIndex={index}
              rowProperties={renderProperties.rowProperties}
              originalRowData={this.props.state.data[data.__metadata.index]}
              styles={styles}
              settings={settings}
              tableProperties={tableProperties}
              ignoredColumns={renderProperties.ignoredColumns}
              columnProperties={renderProperties.columnProperties}
              />
          );

    const { style, className } = getStyleProperties(this.props, 'tableBody');

    return (
      <tbody style={style} className={className}>
        {rows}
      </tbody>
    );
  }
}

export default TableBody;

import React from 'react';
import classnames from 'classnames';

import { getStyleProperties } from './utils/styleHelper';
class TableHeadingCell extends React.Component {
  constructor(props, context) {
    super(props, context);

    this._handleClick = this._handleClick.bind(this);
    this._handleHover = this._handleHover.bind(this);
  }

  getSortIcon() {
    const { sorted, sortAscending, icons } = this.props;

    if (sorted) {
      return sortAscending ? icons.sortAscending : icons.sortDescending;
    }
  }

  isSortable() {
    const { column, renderProperties } = this.props;
    const columnProperties = renderProperties.columnProperties[column];

    if(columnProperties && columnProperties.hasOwnProperty('sortable') && columnProperties.sortable === false) {
      return false;
    }

    return true;
  }


  render() {
    const style = this.props.styles.getStyle({
        styles: this.props.styles.inlineStyles,
        styleName: 'columnTitle',
        mergeStyles: {
          width: this.props.columnProperty.width,
          ...(this.props.alignment || this.props.headerAlignment ? {textAlign: this.props.headerAlignment || this.props.alignment} : {}),
          ...this.props.style
        }
      });

    const { className } = getStyleProperties(this.props, 'tableHeadingCell');
    const classNames = classnames(className, this.props.columnProperty ? this.props.columnProperty.headerCssClassName : null)
    const { sorted } = this.props;
    const clickEvent = this.isSortable() ? this._handleClick : null;

    return (
      <th
        key={this.props.column}
        style={style}
        onMouseOver={this._handleHover}
        onClick={clickEvent}
        className={classNames}
      >
        {this.props.customHeaderComponent ? <this.props.customHeaderComponent {...this.props} /> : this.props.title } { this.getSortIcon() }
      </th>);
  }

  _handleHover() {
    this.props.headingHover(this.props.column);
  }

  _handleClick() {
    this.props.headingClick(this.props.column);
  }
}

TableHeadingCell.propTypes = {
  headingHover: React.PropTypes.func,
  headingClick: React.PropTypes.func,
  column: React.PropTypes.string,
  headerAlignment: React.PropTypes.oneOf(['left', 'right', 'center']),
  alignment: React.PropTypes.oneOf(['left', 'right', 'center']),
  sortAscending: React.PropTypes.bool,
  sorted: React.PropTypes.bool
};

export default TableHeadingCell;

import React from 'react';
import ColumnHelper from './utils/column-helper';
import { getStyleProperties } from './utils/styleHelper';
import { arraysEqual } from './utils/arrayHelper';;

  class TableHeading extends React.Component {
  constructor(props, context) {
    super(props, context);

    this.state = {};
  }

  shouldComponentUpdate(nextProps) {
    //TODO: Make this nicer - shouldn't be reminiscent of clojure level paran usage
    return (!arraysEqual(this.props.columns, nextProps.columns) ||
      ((this.props.pageProperties && nextProps.pageProperties) &&
        (!arraysEqual(this.props.pageProperties.sortColumns, nextProps.pageProperties.sortColumns) ||
        this.props.pageProperties.sortAscending !== nextProps.pageProperties.sortAscending)
      )
    );
  }

  getColumnTitle(column) {
    const initial = this.props.columnTitles[column]  ?
              this.props.columnTitles[column] :
              column;

    return this.props.renderProperties.columnProperties[column] && this.props.renderProperties.columnProperties[column].hasOwnProperty('displayName') ?
        this.props.renderProperties.columnProperties[column].displayName :
        initial
  }

  render() {
    let { headingClick, headingHover } = this.props.events;
    const { renderProperties } = this.props;
    const { style, className } = getStyleProperties(this.props, 'tableHeading');

    const headings = this.props.columns.map(column =>{
      let columnProperty = ColumnHelper.getColumnPropertyObject(renderProperties.columnProperties, column);
      const showColumn = ColumnHelper.isColumnVisible(column, { columnProperties: renderProperties.columnProperties, ignoredColumns: renderProperties.ignoredColumns });
      const sortAscending = this.props.pageProperties && this.props.pageProperties.sortAscending;
      const sorted = this.props.pageProperties && this.props.pageProperties.sortColumns.indexOf(column) > -1

      const title = this.getColumnTitle(column);
      let component = null;
      if(showColumn) {
        component = (<this.props.components.TableHeadingCell
            key={column}
            column={column}
            sorted={sorted}
            sortAscending={sortAscending}
            settings={this.props.settings}
            styles={this.props.styles}
            headingClick={headingClick}
            headingHover={headingHover}
            icons={this.props.styles.icons}
            title={title}
            columnProperty={columnProperty}
            {...columnProperty}
            {...this.props}/>);
      }

      return component;
    });

    return this.props.columns.length > 0 ? (
      <thead style={style} className={className}>
        <tr>
          {headings}
        </tr>
      </thead>
    ) : null;
  }
}

  export default TableHeading;

import React from 'react';
import RowDefinition from './row-definition';
import { getStyleProperties } from './utils/styleHelper';

class Table extends React.Component {
  constructor(props, context) {
    super(props, context);

    this.state = {};
  }

  getTableBodySection() {
    if(this.props.data.length < 1) {
      return (
        <tbody>
          <tr>
            <td>
              <this.props.components.NoResults
                components={this.props.components}
                styles={this.props.styles}
                settings={this.props.settings}
                events={this.props.events} />
            </td>
          </tr>
        </tbody>
      );
    }

    return <this.props.components.TableBody {...this.props} />;
  }

  getColumns() {
    const columnProperties = this.props.renderProperties.columnProperties;

    if(this.props.data.length > 0) {
      return Object.keys(this.props.data[0]);
    }

    if(columnProperties) {
      return Object.keys(this.props.renderProperties.columnProperties).sort((first, second) => {
        const firstColumn = columnProperties[first];
        const secondColumn = columnProperties[second];

        //deal with columns without order properties
        if(!firstColumn['order'] && !secondColumn['order']) { return 0; }
        if(firstColumn['order'] && !secondColumn['order']) { return -1 }
        if(!firstColumn['order'] && secondColumn['order']) { return 1 }

        //order the columns if they both have an order property
        return (firstColumn["order"]) - (secondColumn["order"]);
      });
    }

    return [];
  }

  getTableHeaderSection() {
    const columns = this.getColumns();

    if(columns.length > 0) {
      return <this.props.components.TableHeading columns={columns} {...this.props} />
    }

    return null;
  }

  render() {
    const { settings, styles } = this.props;
    const style = styles.getStyle({
      styles: styles.inlineStyles,
      styleName: 'table',
      mergeStyles: settings.useFixedTable && styles.getStyle({
        styles: styles.inlineStyles,
        styleName: 'fixedTable',
      })
    });

    const { className } = getStyleProperties(this.props, 'table');
    //translate the definition object to props for Heading / Body
    return (
      <table
        className={className}
        style={style}
      >
        { this.getTableHeaderSection() }
        { this.getTableBodySection() }
      </table>
    );
  }
}

